import React, { useState, useRef } from 'react';
import Card from '../UI/Card'
import Button from '../UI/Button'
import ErrorModal from '../UI/ErrorModal'
// import Wrapper from '../Helpers/Wrapper'
import styles from './AddUser.module.css'

const AddUser = (props) => {
    const nameInputRef = useRef();
    const ageInputRef = useRef();

    const [enteredUsername, setEnteredUsername] = useState('');
    const [enteredAge, setEnteredAge] = useState('');
    const [error, setError] = useState();

    const addUserHandler = (event) => {
        event.preventDefault();
        console.log(nameInputRef.current.value);
        if(enteredUsername.trim().length === 0 || enteredAge.trim().length === 0 ){
            setError({title: 'Invalid input', message: 'Please enter a valid name and age.'})
            return;
        }
        if(+enteredAge < 1){ //the + enforces that the string is parsable to a number.
            setError({title: 'Invalid age', message: 'Please enter a valid age. (>0)'})
            return;
        }

        props.onAddUser(enteredUsername, enteredAge);
        setEnteredUsername('');
        setEnteredAge('');
    }

    const usernameChangeHandler = (event) => {
        setEnteredUsername(event.target.value);
    }

    const ageChangeHandler = (event) => {
        setEnteredAge(event.target.value);
    }

    const errorHandler = () => {
        setError(null);
    }

    return (
        <React.Fragment>
        {/* <Wrapper> */}
            {error && <ErrorModal title={error.title} message={error.message} onConfirm={errorHandler}></ErrorModal>}
            <Card className={styles.input}>
                <form onSubmit={addUserHandler}>
                    <label htmlFor="username">Username</label>
                    <input id="username" type="text" value={enteredUsername} onChange={usernameChangeHandler} ref={nameInputRef}/>
                    <label htmlFor="age">Age (Years)</label>
                    <input id="age" type="number" value={enteredAge} onChange={ageChangeHandler} ref={ageInputRef}/>
                    <Button type="submit">Add User</Button>
                </form>
            </Card>
        {/* </Wrapper> */}
        </React.Fragment>
    );
}

export default AddUser;
